sys_script_email.script:
(function runMailScript( /* GlideRecord */ current, /* TemplatePrinter */ template,
    /* Optional EmailOutbound */
    email, /* Optional GlideRecord */ email_action,
    /* Optional GlideRecord */
    event) {

    var rightNow = new GlideDateTime();

    var data = new GlideRecord('x_58872_needit_needit_task');

    data.addQuery('due_date', '<=', rightNow);
    data.addQuery('state', '<', 3);
	data.addQuery('assigned_to',current.assigned_to);
    data.query();

    template.print("In addition to this OverDue NeedIt Task you have other pending tasks:" +(data.getRowCount()-1));

})(current, template, email, email_action, event);
